import React, { useEffect, useState } from "react";
import axios from "axios";

const Game = ({ match }) => {
  const { id } = match.params;
  const { game, setGame } = useState({});

  useEffect(() => {
    axios
      .get(`https://apis.wilders.dev/wild-games/games/${id}/`)
      .then((response) => setGame(response));
  }, [id]);

  return (
    <div>
      <p>{game.name}</p>
    </div>
  );
};

export default Game;
